// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.6.0;

contract Lottery { 

    mapping(address => uint256) public winnings;
    address payable [] public tickets;

	// -------------------- variables -----------------------------------------	
	string public name = "Lottery";
	uint256 public maxTickets = 100;
	uint256 public remainingTickets = 0;
	uint public ticketCount = 0;
	uint256 public randomNum = 0;
	address public latestWinner;
	//wei
	uint256 public ticketPrice = 100000000000000000;

	constructor () public {
	    remainingTickets = maxTickets;
	} 
	
	function Buy() public payable {
	    
	    require(msg.value == ticketPrice);
	    
	    uint256 val = msg.value / ticketPrice;
	    
	    require (remainingTickets - val < remainingTickets);
	    
	    remainingTickets -= val; //remove from unspent supply
	    
	    tickets.push(payable(msg.sender));
	    ticketCount++;
	    
	}
	
	function Withdraw() public payable {
	    require(winnings[msg.sender] > 0);
	    
	    uint256 amountToWithdraw = winnings[msg.sender];
	    winnings[msg.sender] = 0;
	    
	    //in wei number
	    amountToWithdraw *= ticketPrice;
	    
	    payable(msg.sender).transfer(amountToWithdraw);
	    
	}
	
	function chooseWinner() public {
	    
	    require(ticketCount > 0);
	    randomNum = uint(blockhash(block.number-1)) % ticketCount;
	    
	    latestWinner = tickets[randomNum];
	    winnings[latestWinner] = ticketCount;
	    ticketCount = 0;
	    remainingTickets = maxTickets;
	    
	    delete tickets;
	}


	function numTicketsBought() public view returns(uint256) {
		uint num = 0;
		for(uint i = 0; i<tickets.length; i++) {
			if(tickets[i] == msg.sender) {
				num++;
			}
		}
		return num;
	}

	function getRemainingTickets() public view returns(uint256) {
		return remainingTickets;
	}

	function checkBalance() external view returns (uint256) {
		return winnings[msg.sender] * ticketPrice;
	}

}
